mapper.select:
select *
from(
	select rownum as rnum, tmp.*
	from(
		select board_info.*
		from board_info
		where type_seq = 1
		order by board_regdate desc
	) tmp
)
where rnum >= #{startNum} and rnum <= #{endNum}

select *
from(
	select rownum as rnum, tmp.*
	from(
		select board_info.*
		from board_info
		where type_seq = 1 and region_seq = ${regionSeq}
		order by board_regdate desc
	) tmp
)
where rnum >= #{startNum} and rnum <= #{endNum}

select count(*)
from board_info
where type_seq = 1 and region_seq = ${regionSeq}
